INSERT INTO `program` (`id`, `nama_program`, `slug`, `deskripsi`, `kategori`, `metode_pembayaran_ids`, `pilar`, `gambar`, `target_donasi`, `donasi_terkumpul`, `views`, `created_at`) VALUES
(20, 'INFAQ', 'infaq', '🌟 Infaq Umum Lazismu Tulungagung\r\n\r\nInfaq adalah wujud kepedulian sosial dan bukti nyata dari keikhlasan kita dalam berbagi.\r\nBukan seberapa besar jumlah yang diberikan, tetapi seberapa tulus kita memberi untuk kebaikan yang abadi.\r\n\r\nMelalui program Infaq Umum, Lazismu Tulungagung menyalurkan donasi dari para dermawan untuk mendukung berbagai kegiatan sosial, kemanusiaan, pendidikan, dan dakwah.\r\n\r\n🤲 Mari sisihkan sebagian rezeki kita untuk membantu sesama dan menjadi bagian dari keberkahan yang terus mengalir.\r\n\r\n📌 Rekening Infaq\r\n🏦 Bank Syariah Indonesia (BSI)\r\n710 3748 393\r\na.n Lazismu Tulungagung\r\n\r\n📞 0821-2599-1199', 'Infak', '2,6', 'PROGRAM RUTIN', '1756569135_infaq.png', 500000000.00, 25500.00, 69, '2025-08-24 09:49:23'),
(21, 'ZAKAT', 'zakat', 'Zakat adalah kewajiban bagi setiap muslim yang mampu untuk mengeluarkan sebagian hartanya sesuai ketentuan syariat, sebagai bentuk penyucian harta dan kepedulian terhadap sesama. Dana zakat akan disalurkan kepada mustahik yang berhak, seperti fakir, miskin, dan penerima lainnya sesuai dengan ketentuan dalam Al-Qur’an.', 'Zakat', '1,8', 'PROGRAM RUTIN', '1756569152_ZAKAT (1930 x 1080 px).jpg', 512000000.00, 0.00, 61, '2025-08-30 15:07:26'),
(1005, 'Bantu Korban Bencana di Aceh, Sumatra, dan Lumajang', NULL, 'LAZISMU Tulungagung mengajak masyarakat untuk bersama-sama membantu saudara-saudara kita yang sedang tertimpa musibah di Aceh, Sumatra, dan Lumajang. Bencana yang terjadi di tiga wilayah tersebut telah menyebabkan rumah rusak, akses terputus, serta banyak keluarga harus mengungsi dan kehilangan harta benda.', 'Infak', '9', NULL, 'bencanah.png', 100000000.00, 950000.00, 151, '2025-12-02 11:53:19');
